changeSet.sql:
CREATE TABLE Origin ( origin_id varchar(255) NOT NULL, currency varchar(255) DEFAULT NULL, place varchar(255) DEFAULT NULL, era varchar(255) DEFAULT NULL, description varchar(255) DEFAULT NULL, PRIMARY KEY (origin_id) );
CREATE TABLE Product ( product_id varchar(255) NOT NULL, origin_id varchar(255), PRIMARY KEY (product_id), FOREIGN KEY (origin_id) REFERENCES Origin(origin_id) );
CREATE TABLE Coin ( coin_id varchar(255) NOT NULL, status varchar(255) DEFAULT NULL, location varchar(255) DEFAULT NULL, product_id varchar(255), PRIMARY KEY (coin_id), FOREIGN KEY (product_id) REFERENCES Product(product_id) );
